:root {
    --primary-color: #D84315; /* Autumn Orange */
    --secondary-color: #FFE0B2; /* Warm Cream */
    --accent-color: #4E342E; /* Dark Brown */
    --text-color: #3E2723;
    --bg-color: #FFF3E0;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: var(--bg-color);
    color: var(--text-color);
    line-height: 1.6;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

header {
    background-color: var(--primary-color);
    color: white;
    padding: 1rem 0;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.cart-icon {
    font-weight: bold;
    background: rgba(255,255,255,0.25);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    border: 1px solid rgba(255,255,255,0.4);
}

.hero {
    text-align: center;
    padding: 4rem 0;
    background: linear-gradient(rgba(255, 255, 255, 0.8), rgba(255, 255, 255, 0.8)), url('https://images.unsplash.com/photo-1499636138143-bd630876aa52?auto=format&fit=crop&q=80');
    background-size: cover;
    background-position: center;
    margin-bottom: 2rem;
    border-radius: 0 0 10px 10px;
    border-bottom: 5px solid var(--accent-color);
}

.hero h2 {
    font-size: 2.8rem;
    margin-bottom: 0.5rem;
    color: var(--primary-color);
    text-shadow: 1px 1px 2px rgba(0,0,0,0.1);
}

.cookie-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 3rem;
}

.cookie-card {
    background: white;
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 10px rgba(0,0,0,0.05);
    transition: transform 0.2s, box-shadow 0.2s;
    display: flex;
    flex-direction: column;
    border: 1px solid #eee;
}

.cookie-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 15px rgba(0,0,0,0.1);
}

.cookie-image {
    height: 180px;
    background-color: #FFCCBC;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 4rem;
}

.cookie-info {
    padding: 1.5rem;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
}

.cookie-info h3 {
    margin-bottom: 0.5rem;
    color: var(--accent-color);
    font-size: 1.4rem;
}

.desc {
    font-size: 0.9rem;
    color: #666;
    margin-bottom: 1rem;
}

.price {
    font-size: 1.25rem;
    font-weight: bold;
    margin-bottom: 1rem;
    color: var(--primary-color);
}

button.add-btn {
    margin-top: auto;
    background-color: var(--accent-color);
    color: white;
    border: none;
    padding: 0.75rem;
    border-radius: 6px;
    cursor: pointer;
    font-size: 1rem;
    transition: background 0.3s;
}

button.add-btn:hover {
    background-color: #3E2723;
}

footer {
    text-align: center;
    padding: 2rem;
    background-color: var(--accent-color);
    color: rgba(255,255,255,0.8);
    margin-top: auto;
}

const products = [
    { id: 1, name: "Autumn's Pumpkin Spice", price: 3.00, emoji: "üéÉ", desc: "Soft pumpkin cookies with a maple glaze." },
    { id: 2, name: "NM Biscochito", price: 2.00, emoji: "üçÇ", desc: "The state cookie! Anise and cinnamon sugar." },
    { id: 3, name: "Mesilla Pecan Sandie", price: 2.50, emoji: "üå∞", desc: "Buttery shortbread with local pecans." },
    { id: 4, name: "Green Chile Pistachio", price: 3.50, emoji: "üå∂Ô∏è", desc: "Sweet and spicy Las Cruces favorite." },
    { id: 5, name: "Organ Mountain Oats", price: 2.25, emoji: "üèîÔ∏è", desc: "Hearty oatmeal with raisins and cranberries." },
    { id: 6, name: "Classic Choco-Chip", price: 2.50, emoji: "üç™", desc: "Grandma's secret recipe, served warm." }
];

let cartCount = 0;

function init() {
    const productList = document.getElementById('product-list');

    products.forEach(product => {
        const card = document.createElement('div');
        card.className = 'cookie-card';

        card.innerHTML = `
            <div class="cookie-image">${product.emoji}</div>
            <div class="cookie-info">
                <h3>${product.name}</h3>
                <p class="desc">${product.desc}</p>
                <p class="price">$${product.price.toFixed(2)}</p>
                <button class="add-btn" onclick="addToCart('${product.name}')">Add to Cart</button>
            </div>
        `;

        productList.appendChild(card);
    });
}

function addToCart(productName) {
    cartCount++;
    document.getElementById('cart-count').innerText = cartCount;

    // Visual feedback for the button
    const btn = event.target;
    const originalText = btn.innerText;
    const originalColor = btn.style.backgroundColor;

    btn.innerText = "Added!";
    btn.style.backgroundColor = "#2E7D32"; // Green success

    setTimeout(() => {
        btn.innerText = originalText;
        btn.style.backgroundColor = originalColor;
    }, 1000);

    console.log(`Added ${productName} to cart.`);
}

document.addEventListener('DOMContentLoaded', init);

